// ============ CONFIGURATION ============
// Edit these values to customize the workflow

const CONFIG = {
  // Set to true for test mode (no status updates to sheet)
  DRY_RUN_MODE: true,

  // Set to true to stop all processing
  GLOBAL_PAUSE: false,

  // Your notification email
  NOTIFICATION_EMAIL: 'vedkolambkaterna@gmail.com',

  // Personal details for cover letters
  APPLICANT_NAME: 'Ved Mukund Kolambikar',
  APPLICANT_EMAIL: 'vedmukundkolambikar@gmail.com',
  APPLICANT_PHONE: '+49 155 10820660',
  APPLICANT_LOCATION: 'Offenburg, Baden-WÃ¼rttemberg, Germany',

  // Rate limit delay in ms between jobs
  RATE_LIMIT_DELAY_MS: 3000
};

// ============ END CONFIGURATION ============

return { json: CONFIG };

const config = $input.item.json;

if (config.GLOBAL_PAUSE === true) {
  // Stop processing
  return [];
}

// Pass config forward
return [{ json: config }];

// Filter only NEW jobs and check for duplicates
const allJobs = $input.all();
const config = $('CONFIG').first().json;

const newJobs = allJobs.filter(item => {
  const status = (item.json.status || '').trim().toUpperCase();
  return status === 'NEW';
});

if (newJobs.length === 0) {
  return [];
}

// Check each new job for duplicates
const results = [];

for (const newJob of newJobs) {
  const company = (newJob.json.company || '').toLowerCase().trim();
  const jobTitle = (newJob.json.job_title || '').toLowerCase().trim();
  const jobUrl = (newJob.json.job_url || '').toLowerCase().trim();
  const jobId = (newJob.json.job_id || '').trim();

  let isDuplicate = false;
  let duplicateReason = '';

  for (const existingJob of allJobs) {
    const existingId = (existingJob.json.job_id || '').trim();
    const existingStatus = (existingJob.json.status || '').toUpperCase().trim();

    if (existingId === jobId) continue;
    if (existingStatus !== 'APPLIED') continue;

    const existingUrl = (existingJob.json.job_url || '').toLowerCase().trim();
    const existingCompany = (existingJob.json.company || '').toLowerCase().trim();
    const existingTitle = (existingJob.json.job_title || '').toLowerCase().trim();

    if (jobUrl && existingUrl && jobUrl === existingUrl) {
      isDuplicate = true;
      duplicateReason = 'Duplicate URL - already applied (job_id: ' + existingId + ')';
      break;
    }

    if (company && existingCompany && company === existingCompany &&
        jobTitle && existingTitle && jobTitle === existingTitle) {
      isDuplicate = true;
      duplicateReason = 'Duplicate company+role - already applied (job_id: ' + existingId + ')';
      break;
    }
  }

  results.push({
    json: {
      ...newJob.json,
      isDuplicate,
      duplicateReason,
      processedAt: new Date().toISOString(),
      config: config
    }
  });
}

return results;

const job = $input.item.json;

if (job.isDuplicate) {
  return {
    json: {
      ...job,
      finalStatus: 'SKIPPED',
      finalNotes: 'DUPLICATE: ' + job.duplicateReason
    }
  };
}

return { json: { ...job, isDuplicate: false } };

// Fetch job page and extract keywords - all in one node
const job = $input.item.json;
const config = job.config;

let jobDescription = '';
let extractionMethod = '';

// Try to fetch the job page
try {
  if (job.job_url) {
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 8000);
    const response = await fetch(job.job_url, { signal: controller.signal });
    clearTimeout(timeoutId);

    if (response.ok) {
      const html = await response.text();
      if (html && html.length > 100) {
        let text = html;
        text = text.replace(new RegExp('<script[^>]*>[\\s\\S]*?</script>', 'gi'), '');
        text = text.replace(new RegExp('<style[^>]*>[\\s\\S]*?</style>', 'gi'), '');
        text = text.replace(/<[^>]+>/g, ' ');
        text = text.replace(/&nbsp;/g, ' ');
        text = text.replace(/&amp;/g, '&');
        text = text.replace(/\s+/g, ' ');
        text = text.trim();
        jobDescription = text.substring(0, 5000);
        extractionMethod = 'html_fetch';
      }
    }
  }
} catch (e) {
  // Fetch failed - use fallback
}

// Fallback if fetch didn't work
if (!jobDescription) {
  if (job.job_description && job.job_description.trim()) {
    jobDescription = job.job_description.trim();
    extractionMethod = 'manual_input';
  } else {
    jobDescription = 'Role: ' + (job.job_title || 'Unknown') + ' at ' + (job.company || 'Unknown') + '. Location: ' + (job.location || 'Unknown');
    extractionMethod = 'basic_info_only';
  }
}

// Extract keywords by simple string matching
const keywordList = ['python', 'javascript', 'react', 'nodejs', 'security', 'penetration', 'osint', 'vapt', 'compliance', 'iso 27001', 'gdpr', 'aws', 'azure', 'cloud', 'docker', 'kubernetes', 'machine learning', 'ml', 'ai', 'nlp', 'api', 'sql', 'linux', 'bash', 'git', 'siem', 'splunk', 'wireshark', 'nmap', 'red team', 'blue team', 'soc', 'incident'];

const descLower = jobDescription.toLowerCase();
const extractedKeywords = keywordList.filter(kw => descLower.includes(kw));

return {
  json: {
    job_id: job.job_id,
    job_title: job.job_title,
    company: job.company,
    job_url: job.job_url,
    location: job.location,
    source: job.source,
    processedAt: job.processedAt,
    jobDescription: jobDescription,
    extractedKeywords: extractedKeywords,
    extractionMethod: extractionMethod,
    config: config
  }
};

// Generate cover letter based on job keywords
const job = $input.item.json;
const config = job.config;

const applicantName = config.APPLICANT_NAME;
const applicantEmail = config.APPLICANT_EMAIL;
const applicantPhone = config.APPLICANT_PHONE;
const applicantLocation = config.APPLICANT_LOCATION;

const companyName = job.company || '[Company Name]';
const jobTitle = job.job_title || '[Position]';
const jobLocation = job.location || '';
const keywords = job.extractedKeywords || [];

// Determine focus area
let focusArea = 'cybersecurity';
let relevantSkills = 'OSINT, penetration testing, security compliance, and threat intelligence';
let specificExp = 'conducting VAPT assessments, implementing ISMS frameworks, and building automated security tools';

if (keywords.some(k => ['python', 'ml', 'machine learning', 'ai', 'nlp'].includes(k))) {
  focusArea = 'AI/ML and security';
  relevantSkills = 'Python, machine learning, NLP, and AI-driven security solutions';
  specificExp = 'developing AI-powered security tools, RAG-based compliance systems, and ML model deployment';
}
if (keywords.some(k => ['compliance', 'iso 27001', 'gdpr'].includes(k))) {
  focusArea = 'security compliance';
  relevantSkills = 'ISO 27001, ISMS, compliance auditing, and risk assessment';
  specificExp = 'implementing ISMS frameworks, conducting compliance audits, and ISO 27001 documentation';
}
if (keywords.some(k => ['osint', 'penetration', 'vapt', 'red team', 'soc', 'incident'].includes(k))) {
  focusArea = 'offensive security';
  relevantSkills = 'OSINT, VAPT, penetration testing, and red team operations';
  specificExp = 'adversarial assessments, attack surface analysis, MITRE ATT&CK mapping, and threat simulation';
}

const today = new Date().toLocaleDateString('en-GB', { day: 'numeric', month: 'long', year: 'numeric' });

const coverLetter = applicantName + '\n' + applicantLocation + '\nEmail: ' + applicantEmail + '\nPhone: ' + applicantPhone + '\n\n' + today + '\n\nDear Hiring Team at ' + companyName + ',\n\nI am writing to express my strong interest in the ' + jobTitle + ' position at ' + companyName + '. With my background in ' + focusArea + ' and hands-on experience in ' + relevantSkills + ', I am confident in my ability to contribute meaningfully to your team.\n\nDuring my roles at Cyber Secured India and V.I.E.H Group, I developed practical expertise in ' + specificExp + '. My academic foundation includes a Bachelors in Computer Engineering from the University of Mumbai, where my thesis focused on NLP-based text summarization with CI/CD integration.\n\nKey highlights of my experience include:\n- Conducting comprehensive VAPT assessments and security audits\n- Implementing ISMS frameworks aligned with ISO 27001 standards\n- Developing AI-driven security tools using Python, ML models, and RAG architectures\n- Working with cloud platforms (AWS, Azure) and security tools (Wireshark, Nmap, Splunk)\n\nI am particularly drawn to ' + companyName + ' because of the opportunity to apply my skills in a challenging environment while continuing to grow professionally.' + (jobLocation ? ' I am open to ' + (jobLocation.toLowerCase().includes('remote') ? 'remote work' : 'relocation to ' + jobLocation) + ' as required.' : '') + '\n\nI would welcome the opportunity to discuss how my background aligns with your needs. Thank you for considering my application.\n\nWarm regards,\n' + applicantName;

const coverLetterVersion = 'v1_' + focusArea.replace(/[^a-z]/gi, '_') + '_' + new Date().toISOString().split('T')[0];

// Determine final status based on dry run mode
let finalStatus = 'MANUAL REQUIRED';
let finalNotes = 'Cover letter ready (' + focusArea + ' focus). Keywords: ' + keywords.join(', ') + '. SUBMIT MANUALLY at: ' + (job.job_url || 'N/A');

if (config.DRY_RUN_MODE === true) {
  finalStatus = 'DRY-RUN';
  finalNotes = '[DRY RUN] Cover letter generated (' + focusArea + ' focus). Keywords: ' + keywords.join(', ') + '. Extraction: ' + job.extractionMethod;
}

return {
  json: {
    job_id: job.job_id,
    job_title: job.job_title,
    company: job.company,
    job_url: job.job_url,
    location: job.location,
    source: job.source,
    processedAt: job.processedAt,
    extractedKeywords: keywords,
    extractionMethod: job.extractionMethod,
    generatedCoverLetter: coverLetter,
    coverLetterFocus: focusArea,
    coverLetterVersion: coverLetterVersion,
    finalStatus: finalStatus,
    finalNotes: finalNotes,
    config: config
  }
};
